const extractSubjectChapterLosFromQuestion = (subject) => {
  const result = {
    subject: null,
    chapter: null,
    los: null,
  };

  function traverse(node) {
    if (!node) return;

    if (node.type === "subject") {
      result.subject = {
        id: node.id,
        name: node.name,
        order: node.order,
      };
    } else if (node.type === "chapter") {
      result.chapter = {
        id: node.id,
        name: node.name,
        order: node.order,
      };
    } else if (node.type === "los") {
      result.los = {
        id: node.id,
        name: node.name,
        order: node.order,
      };
    }

    if (node.Subject) {
      traverse(node.Subject);
    }
  }

  traverse(subject.Subject);
  return result;
};

const extractStatusAndTimeTaken = (question) => {
  const timeTaken = answers?.find(
    (answer) => answer?.questionId === question.Question.id
  ).timeTaken;

  const isCorrect = answers?.find(
    (answer) => answer?.questionId === question.Question.id
  ).Option?.RightOption?.optionId
    ? true
    : false;

  const isAttempted = answers?.find(
    (answer) => answer?.questionId === question.Question.id
  ).optionId
    ? true
    : false;

  let status = "";

  if (isCorrect) {
    status = "correct";
  } else {
    if (isAttempted) {
      status = "wrong";
    } else {
      status = "unattempted";
    }
  }

  return { timeTaken, status };
};

export const getAllSubjects = (data) => {
  const questionsData = [];

  for (let i = 0; i < data.length; i++) {
    const question = data[i];
    const { status, timeTaken } = extractStatusAndTimeTaken(question);
    const belongsTo = extractSubjectChapterLosFromQuestion(
      question?.Question?.FallNumber[0]?.FallNumber?.Subject[0]
    );
    const subject = belongsTo?.subject;
    const subjectExists = questionsData?.find((item) => {
      return item.id === subject.id;
    });

    const questionToAdd = {
      id: question.Question.id,
      optionId: question.optionId,
      order: question.order,
      question: question.Question.question,
      options: question.Question.Option,
      hasSubmitted: question.hasSubmitted,
      questionId: question.Question.id,
      UserFlag: question.Question.UserFlag,
      Question: question.Question.Question,
      score: question.Question.score,
      averageTime: question.Question.averageTime,
      difficulty: question.Question.difficulty,
      attribute: question.Question.attribute,
      createdAt: question.Question.createdAt,
      updatedAt: question.Question.updatedAt,
      attachment: question.Question.attachment,

      timeTaken: timeTaken,
      status: status,
    };

    if (subjectExists) {
      //then i need to check topic
      const topic = belongsTo?.chapter;
      const topicExists = subjectExists?.topics?.find(
        (item) => item.id === topic.id
      );

      if (topicExists) {
        // add question removing th fallnumbr key
        topicExists?.questions.push(questionToAdd);
      } else {
        subjectExists?.topics.push({
          // i want all keys of topic except Subject
          id: belongsTo?.chapter.id,
          name: belongsTo?.chapter.name,
          questions: [questionToAdd],
        });
      }
    } else {
      questionsData?.push({
        id: subject.id,
        name: subject.name,
        order: subject.order,
        type: subject.type,
        topics: [
          {
            id: belongsTo?.chapter.id,
            name: belongsTo?.chapter.name,
            questions: [questionToAdd],
          },
        ],
      });
    }
  }
  return questionsData;
};

export const run = () => {
  const data = getAllSubjects(newData);
  return data;
};
